<Typewriter text="Hello, world!" variant="outline" />

// With sizes
<Typewriter text="Hello, world!" size="sm" />
<Typewriter text="Hello, world!" size="md" />
<Typewriter text="Hello, world!" size="lg" />

// With states
<Typewriter text="Hello, world!" isLoading />
